 */

/**
 * The public-facing functionality of the plugin.
 *
 * Defines the plugin name, version, and two examples hooks for how to
 * enqueue the public-facing stylesheet and JavaScript.
 *
 * @package    Addressautofill
 * @subpackage Addressautofill/public
 * @author     Sunil Ramsinghani <s.ramsinghani@pataa.com>
 */

 class Addressautofill_Public {

	/**
	 * The ID of this plugin.
	 *
	 * @since    1.0.0
	 * @access   private
	 * @var      string    $plugin_name    The ID of this plugin.
	 */
	private $plugin_name;

	/**
	 * The version of this plugin.
	 *
	 * @since    1.0.0
	 * @access   private
	 * @var      string    $version    The current version of this plugin.
	 */
	private $version;

	/**
	 * Initialize the class and set its properties.
	 *
	 * @since    1.0.0
	 * @param      string    $plugin_name       The name of the plugin.
	 * @param      string    $version    The version of this plugin.
	 */
	public function __construct( $plugin_name, $version ) {

		$this->plugin_name = $plugin_name;
		$this->version = $version;
		add_action('woocommerce_checkout_update_order_meta','addMetatoOrders', 10, 2);

	}

	/**
	 * Register the stylesheets for the public-facing side of the site.
	 *
	 * @since    1.0.0
	 */
	public function enqueue_styles() {

		/**
		 * This function is provided for demonstration purposes only.
		 *
		 * An instance of this class should be passed to the run() function
		 * defined in Addressautofill_Loader as all of the hooks are defined
		 * in that particular class.
		 *
		 * The Addressautofill_Loader will then create the relationship
		 * between the defined hooks and the functions defined in this
		 * class.
		 */

		wp_enqueue_style( $this->plugin_name, plugin_dir_url( __FILE__ ) . 'css/addressautofill-public.css', array(), $this->version, 'all' );

	}

	/**
	 * Register the JavaScript for the public-facing side of the site.
	 *
	 * @since    1.0.0
	 */
	public function enqueue_scripts() {

		/**
		 * This function is provided for demonstration purposes only.
		 *
		 * An instance of this class should be passed to the run() function
		 * defined in Addressautofill_Loader as all of the hooks are defined
		 * in that particular class.
		 *
		 * The Addressautofill_Loader will then create the relationship
		 * between the defined hooks and the functions defined in this
		 * class.
		 */
		wp_enqueue_script( $this->plugin_name, plugin_dir_url( __FILE__ ) . 'js/addressautofill-public.js', array( 'jquery' ), $this->version, false );
		$api_key = get_option("settings_page_example_setting");
		$merchant_id = get_option("settings_page_example_setting_1");
		$scriptData = array(
        'api_key' => $api_key,
		'merchant_id' => $merchant_id,
    	);
		wp_localize_script($this->plugin_name, 'my_key', $scriptData);
		//wp_enqueue_script( 'custom', 'https://assets.pataa.com/create-pataa-minified/create-pataa.js', array( 'jquery' ), $this->version, false );
		wp_enqueue_script( 'custom', 'https://pataa.in/pataaweb/developer/create-pataa.js', array( 'jquery' ), $this->version, false );
		

	}


	public function custom_shop_order_column($columns)
	{
		$reordered_columns = array();
	
		// Inserting columns to a specific location
		foreach( $columns as $key => $column){
			$reordered_columns[$key] = $column;
			if( $key ==  'order_status' ){
				// Inserting after "Status" column
				$reordered_columns['pataacode'] = __( 'Pataa Code','theme_domain');
				$reordered_columns['dlink'] = __( 'Pataa Link','theme_domain');
			}
		}
		return $reordered_columns;
	}


	public function custom_orders_list_column_content( $column, $post_id )
	{
		global $post, $the_order;

		$order_id = $the_order->get_id();
		
		switch ( $column )
		{
			case 'pataacode' :
				// Get custom post meta data
				// Loop through order items
				$display = array();
				foreach ( $the_order->get_items() as $item_id => $item ) {
					// $item_id is the current Item ID
		
					// Get the WC_Product Object
					$product = $item->get_product();
					
					// Get custom order item meta data 
					$meta_value = $item->get_meta('pataacode');
					
					$display[$item_id] = $meta_value;
				}
				// Testing output
				echo implode(' | ', $meta_value);
	
				break;
	
			case 'dlink' :
				// Get custom post meta data
				$my_var_two = wc_get_order_item_meta( $post_id, 'dlink', true );
				if(!empty($my_var_two))
					echo $my_var_two;
				break;
		}
	}

	 /**
	 * Add Pataa Code and Dlink to order.
	 *
	 * @param WC_Order_Item_Product $item
	 * @param string                $cart_item_key
	 * @param array                 $values
	 * @param WC_Order              $order
	 */
	

	public function addMetatoOrders( $order_id, $posted ) {
		$order = wc_get_order( $order_id );
		$order->update_meta_data( 'my_custom_meta_key', 'my data' );
		$order->save();
	} 

